import React from "react";
import { useEffect, useState } from "react";
import Header from "./components/Header/Header";
import Intro from "./components/Intro/Intro";
import MenuList from "./components/Menu/MenuList";
import { foodItems, drinkItems } from "./dummy-meals";
import useRequest from "./hooks/use-request";
//
function App() {
  const [foods, setFoods] = useState([]);
  const { isLoading, error, sendRequest } = useRequest();

  useEffect(() =>{
    const printData = (data) => {console.log(data.split('}')[0])};
    sendRequest({}, printData)
  },[sendRequest]
  );

  return (
    <React.Fragment>
      {isLoading && <p>Loading...</p>}
      {error && <p>{error.message}</p>}
      <Header />
      <Intro />
      <MenuList title="Food" menuList={foodItems} />
      <MenuList title="Drinks" menuList={drinkItems} />
    </React.Fragment>
  );
}

export default App;
