import { useContext } from "react";
import Button from "../../UI/Button/Button";
import classes from "./OrderForm.module.css";
import cartContext from "../../../store/shopping-cart-context";

const OrderForm = (props) => {
    const cartCtx = useContext(cartContext); 
  return (
    <form className={classes.form}>
      <div className={classes.controlGroup}>
        <div className={classes.control}>
          <label htmlFor="fname">First Name</label>
          <input
            type="text"
            id="fname"
            // value={firstNameValue}
            // onChange={firstNameValueChangeHandler}
            // onBlur={firstNameBlurrHandler}
          />
          {/* {firstNameHasError && <p>First Name cannot be empty</p>} */}
        </div>
      </div>
      <div className={classes.actions}>
        <button type="submit">Place Order</button>
        <Button className={classes.button} onClick={cartCtx.onPlaceOrder}>
            Place Order
        </Button>
        <button onClick={props.onResolve}>Go back</button>
        <Button onClick={props.onResolve}>Go back</Button>
      </div>
    </form>
  );
};

export default OrderForm;
