import React, { useContext, useState } from "react";
import cartContext from "../../store/shopping-cart-context";
import Button from "../UI/Button/Button";
import CartIcon from "./CartIcon";
import classes from './HeaderCartButton.module.css';

const HeaderCartButton = (props) =>{
    const cartCtx = useContext(cartContext);
    const [viewCart, setViewCart] = useState(false);

    const onViewCart = () =>{
        console.log(cartCtx.currentItems.length);
        setViewCart(true);

    }
    return (
        <button className={classes.button} onClick={onViewCart}><span className={classes.badge}>{cartCtx.currentItems.length}</span><CartIcon className={classes.icon}/><center>{props.children}</center></button>
    )
};

export default HeaderCartButton;
