import React from "react";
import { useEffect, useState } from "react";
import Header from "./components/Header/Header";
import Intro from "./components/Intro/Intro";
import MenuList from "./components/Menu/MenuList";
import { foodItems, drinkItems } from "./dummy-meals";
import useRequest from "./hooks/use-request";
//
function App() {
  const [foods, setFoods] = useState([]);
  const { isLoading, error, sendRequest } = useRequest();

  useEffect(() => {
    // const foodJson = JSON.stringify(foodItems);
    const transformData = (data) => {
      const transformedData = [];
      for(let item of data){
        transformedData.push(
          {
            id: item.id,
            name: item.name,
            description: item.description,
            price: item.price,
          }
        )
      };
      console.log(transformedData);
      setFoods(transformedData);
    };

    sendRequest(
      {url: "https://react-http-48ff4-default-rtdb.firebaseio.com/foods.json",}, transformData
      );


  }, [sendRequest, foods]);

  return (
    <React.Fragment>
      {isLoading && <p>Loading...</p>}
      {error && <p>{error.message}</p>}
      <Header />
      <Intro />
      <MenuList title="Food" menuList={foodItems} />
      <MenuList title="Drinks" menuList={drinkItems} />
    </React.Fragment>
  );
}

export default App;
