import React, { useState, useContext, useRef } from "react";
import cartContext from "../../store/shopping-cart-context";
import Button from "../UI/Button/Button";
import classes from './MenuItemForm.module.css';


const MenuItemForm = (props) =>{
    const cartCtx = useContext(cartContext);

    const inputRef = useRef();
    // const [enteredAmount, setEnteredAmount] = useState('');

    // const inputChangeHandler = (event) =>{
    //     setEnteredAmount(event.target.value);
    // };

    const submitHandler = (event)=> {
        event.preventDefault();
        // if(enteredAmount > 0){
        //     cartCtx.onAddItem({...props, amount: +enteredAmount});
        //     setEnteredAmount('');
        //     console.log(cartCtx.currentItems);  onChange={inputChangeHandler} 
        // }
        if(inputRef.current.value > 0){
            cartCtx.onAddItem({...props, amount: +inputRef.current.value});
            inputRef.current.value = '';
            console.log(cartCtx.currentItems);
        }

    };



    return (
        <form onSubmit={submitHandler} className={classes.form}>
            <div>
                <input type='number' name="amount" id={props.key} ref={inputRef} />
                <label htmlFor={props.key}>Select Amount</label>
            </div>

            {inputRef.current.value >= 0 && <Button type='submit'>Add to Cart</Button>}
            {inputRef.current.value < 0 && <Button type='submit'>Remove from Cart</Button>}


        </form>
    )

};

export default MenuItemForm;
