import React, { useContext, useState } from "react";
import cartContext from "../../store/shopping-cart-context";
import Modal from "../Cart/Modal";
import Button from "../UI/Button/Button";
import CartIcon from "./CartIcon";
import classes from "./HeaderCartButton.module.css";

const HeaderCartButton = (props) => {
  const cartCtx = useContext(cartContext);
  const [viewCart, setViewCart] = useState(false);

  const numberOfCartItems = cartCxt.currentItems.reduce((currentNumber, item) =>{
    currentNumber + item.amount;
  }, 0);

  const onViewCart = () => {
    console.log(cartCtx.currentItems.length);
    setViewCart(true);
  };
  const onCloseCart = () => {
    setViewCart(false);
    cartCtx.setPlacedOrder(false);
  };

  return (
    <React.Fragment>
      {viewCart && <Modal onResolve={onCloseCart} />}
      <Button className={classes.button} onClick={onViewCart}>
        <span className={classes.icon}><CartIcon className={classes.icon} /></span>
        <span>{props.children}</span>
        <span className={classes.badge}>
          {numberOfCartItems}
        </span>
      </Button>
    </React.Fragment>
  );
};

export default HeaderCartButton;
