import React, { useContext } from "react";
import cartContext from "../../store/shopping-cart-context";
import Button from "../UI/Button/Button";
import CartIcon from "./CartIcon";
import classes from './HeaderCartButton.module.css';

const HeaderCartButton = (props) =>{
    const cartCtx = useContext(cartContext);
    return (
        <button className={classes.button} onClick={props.onClick}>{cartCtx.currentItems.length > 0 && <span className={classes.badge}>{cartCtx.currentItems.length}</span>}<CartIcon className={classes.icon}/><center>{props.children}</center></button>
    )
};

export default HeaderCartButton;
