import React, { useContext, useState } from "react";
import cartContext from "../../store/shopping-cart-context";
import Modal from "../Cart/Modal";
import Button from "../UI/Button/Button";
import CartIcon from "./CartIcon";
import classes from './HeaderCartButton.module.css';

const HeaderCartButton = (props) =>{
    const cartCtx = useContext(cartContext);
    const [viewCart, setViewCart] = useState(false);

    const onViewCart = () =>{
        console.log(cartCtx.currentItems.length);
        setViewCart(true);

    };
    const onCloseCart = () =>{
        setViewCart(false);
    };


    return (
        <React.Fragment>
        {viewCart && (<Modal onResolve={onCloseCart}/>)}
        <button className={classes.button} onClick={onViewCart}><span className={classes.badge}>{cartCtx.currentItems.length}<CartIcon className={classes.icon}/></span><center>{props.children}</center></button>
        </React.Fragment>
    )
};

export default HeaderCartButton;
