import React from "react";
import ReactDOM from "react-dom";

import Card from "./Card";
import classes from "./Modal.module.css";
import Cart from "./Cart";

const Backdrop = (props) => {
  return <div className={classes.backdrop} onClick={props.onResolve} />;
};
const ModalOverlay = (props) => {
  return (
    <Card className={classes.modal}>
     <Cart onResolve={props.onResolve} />
    </Card>
  );
};
const Modal = (props) => {
  return (
    <React.Fragment>
      {ReactDOM.createPortal(
        <Backdrop onResolve={props.onResolve} />,
        document.getElementById("backdrop-root")
      )}
      {ReactDOM.createPortal(
        <ModalOverlay onResolve={props.onResolve} title={props.title} message={props.message} />,
        document.getElementById("overlay-root")
      )}
    </React.Fragment>
  );
};

export default Modal;
